      confirmButtonColor: "#7966FF",
      cancelButtonText: "아니오",
      cancelTextColor: "#7966FF",
      Buttons: true,
    }).then((result) => {
      if (result.isConfirmed) {
        dispatch(commentActions.editCommentDB(commentEditId, comment, pid));
      } else {
        return;
      }
    });
  };

  const commentDelete = (e) => {
    Swal.fire({
      title: "댓글을 삭제하시겠습니까?",
