                              ) : (
                                <div>
                                  <div className='mycomment'>
                                    <div>
                                      <Profile
                                        size={24}
                                        imgUrl={list.userImage}
                                      />
                                      <strong>{list.nickname}</strong>
                                    </div>
                                    <div className='button'>
                                      <button
                                        className='comment'
                                        onClick={() => {
                                          setCommentEdit(list.commentId);
                                          setIsCommentEdit(!isCommentEdit);
