import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useSelector, useDispatch } from "react-redux";
import { actionCreators as passWordAction } from "../redux/modules/user";
import { useParams } from "react-router-dom";

const uid = sessionStorage.getItem("uid");

const Password = () => {
  const dispatch = useDispatch();
  const [password, setPassword] = useState("");
  const [newPassword, setnewPassWord] = useState("");
  const [checkPassWord, setCheckPassWord] = useState("");

  const [passwordRuleCheck, setPasswordRuleCheck] = React.useState(false);

  const Pass = (e) => {
    const rule = /^(?=.*\d)(?=.*[a-zA-Z])[0-9a-zA-Z]{4,10}$/;
    const password = e.target.value;
    setPassword(password);
    rule.test(password)
      ? setPasswordRuleCheck(true)
      : setPasswordRuleCheck(false);
  };

  const NewPass = (e) => {
    const rule = /^(?=.*\d)(?=.*[a-zA-Z])[0-9a-zA-Z]{4,10}$/;
    const newPassword = e.target.value;
    setnewPassWord(newPassword);
    rule.test(newPassword)
      ? setPasswordRuleCheck(true)
      : setPasswordRuleCheck(false);
  };

  const CheckPass = (e) => {
    setCheckPassWord(e.target.value);
    console.log(checkPassWord);
  };

  /////////////////입력한 현재비밀번호가 맞는지 확인해야함
  const NewPassWord = () => {
    if (password === newPassword) {
      window.alert("현재비밀번호와 동일합니다.");
      return;
    } else if (newPassword !== checkPassWord) {
      window.alert("입력하신 새비밀번호가 다릅니다.");
      return;
    } else {
      window.confirm("수정하시겠습니까?");
      dispatch(
        passWordAction.NewPassWordDB(uid, password, newPassword, checkPassWord)
      );
    }
  };

  return (
    <Container>
      <div className='title'>비밀번호 재설정</div>

      <div className='user'>
        <div className='inputBox'>
          <div>
            <input
              type='password'
              placeholder='현재 비밀번호'
              onChange={Pass}
            />
            {password.length > 0 && !passwordRuleCheck && (
              <span className='rule'> *영문 4자 이상 입력해주세요. </span>
            )}
          </div>

          <input
            type='passWord'
            placeholder='새로운 비밀번호'
            onChange={NewPass}></input>

          <input
            type='passWord'
            placeholder='새로운 비밀번호 재확인'
            onChange={CheckPass}></input>
          <button onClick={NewPassWord}>저장하기</button>
        </div>
      </div>
    </Container>
  );
};

const Container = styled.div`
  width: 1440px;
  height: 830px;
  margin: auto;
  > div.title {
    width: 160px;
    height: 35px;
    font-size: 24px;
    font-weight: 600;
  }
  > div.user {
    width: 500px;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: auto;
    > div.inputBox {
      width: 500px;
      height: 312px;
      display: flex;
      flex-direction: column;
      justify-content: space-evenly;
      div {
        width: 100%;
        height: 72px;
        display: flex;
        flex-direction: column;
        position: relative;
        justify-content: end;
        background-color: #f9f8ff;
        border-radius: 0.5rem;
        padding: 15px;
        > span.rule {
          color: red;
          font-size: 12px;
          position: absolute;
          justify-content: start;
          padding-left: 15px;
        }
        > input {
          width: 90%;
          height: 32px;
          display: flex;
          align-items: center;
          border: 0px solid black;
          font-size: 16px;
        }
      }
      > input {
        width: 500px;
        height: 72px;
        border-radius: 0.5rem;
        background-color: #f9f8ff;
      }
      > button {
        width: 500px;
        height: 72px;
        color: #f9f8ff;
        background-color: #7966ff;
        border-radius: 0.5rem;
      }
    }
  }
`;

export default Password;
